package in.tanjo.clearmind.base;import android.app.Activity;import android.content.Intent;public class BaseActivity extends Activity {    public <T> void launchActivity(final Class<T> cls) {        Intent intent = new Intent(this, cls);        intent.addFlags(Intent.FLAG_ACTIVITY_NEW_TASK);        intent.addFlags(Intent.FLAG_ACTIVITY_CLEAR_TOP);        try {            startActivity(intent);            finish();        } catch (Exception e) {        }    }}